  private def checkLeader() {
       /* INSERT YOUR CODE HERE */
        var tempBallots=ballots;
       tempBallots+=(self->ballot);
     var (topProcess,topBallot) = tempBallots.maxBy(_._2);
    var top:(Address,Long)=(topProcess,topBallot);
    if(topBallot<highestBallot)
    {
        while(ballot<=highestBallot)
        {
            ballot=incrementBallot(ballot);
        }
        leader=None;
    }
    else
    {
        if(Some(top)!=leader)
        {
            highestBallot=topBallot;
            leader=Some(top);
            println("leader is"+leader);
            trigger(BLE_Leader(topProcess,topBallot)->ble);
        }
    }

  }

  ctrl uponEvent {
    case _: Start => handle {
      startTimer(period);
    }
  }

  timer uponEvent {
    case CheckTimeout(_) => handle {
    /* INSERT YOUR CODE HERE */
    if((ballots.size+1)>=majority)
    {
        checkLeader();
    }
    ballots = mutable.Map.empty[Address, Long];
    round+=1;
    for(p<-topology)
    {
        if(p!=self)
        {
            trigger(PL_Send(p,HeartbeatReq(round,highestBallot))->pl);
        }
    }
      startTimer(period);
  }
  }

  pl uponEvent {
    case PL_Deliver(src, HeartbeatReq(r, hb)) => handle {
         /* INSERT YOUR CODE HERE */
          if(hb>highestBallot)
         {
             highestBallot=hb;
         }
          trigger(PL_Send(src,HeartbeatResp(r,ballot))->pl);
    }
    case PL_Deliver(src, HeartbeatResp(r, b)) => handle {
      /* INSERT YOUR CODE HERE */
        if(r==round)
      {
          ballots+=(src->b);
      }
      else
      {
          period=period+delta;
      }

    }
  }
